/**
 * Claude Command Toolkit Types
 */
/**
 * Claude Code命令的完整参数
 */
export interface CommandOptions {
    /** 命令名称（必需） */
    name: string;
    /** 提示词内容（必需） */
    content: string;
    /** 命令描述（可选，默认从name生成） */
    description?: string;
    /** 使用的Claude模型（可选） */
    model?: string;
    /** 参数提示（可选） */
    argumentHint?: string;
    /** 允许的工具列表（可选） */
    allowedTools?: string[];
    /** 自定义frontmatter字段（可选） */
    customFields?: Record<string, any>;
    /** 目标安装目录（可选，默认 ./.claude/commands） */
    targetDir?: string;
}
/**
 * 命令安装结果
 */
export interface CommandResult {
    /** 安装是否成功 */
    success: boolean;
    /** 命令名称 */
    commandName: string;
    /** 安装的文件路径 */
    filePath?: string;
    /** 结果消息 */
    message: string;
    /** 错误信息（如果失败） */
    error?: string;
    /** 生成的文件内容（用于调试） */
    generatedContent?: string;
}
/**
 * Claude Code Subagent的完整参数
 */
export interface SubagentOptions {
    /** subagent名称（必需） */
    name: string;
    /** 何时调用此subagent的描述（必需） */
    description: string;
    /** subagent的system prompt内容（必需） */
    content: string;
    /** 允许的工具列表（可选，如果省略则继承所有工具） */
    tools?: string[];
    /** 目标安装目录（可选，默认 ./.claude/agents） */
    targetDir?: string;
}
/**
 * Subagent安装结果
 */
export interface SubagentResult {
    /** 安装是否成功 */
    success: boolean;
    /** subagent名称 */
    subagentName: string;
    /** 安装的文件路径 */
    filePath?: string;
    /** 结果消息 */
    message: string;
    /** 错误信息（如果失败） */
    error?: string;
    /** 生成的文件内容（用于调试） */
    generatedContent?: string;
}
